package basic;
public class AlphabetOrNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char ch = '+';
		
		if((ch>='a' && ch<='z')||(ch>='A' && ch<='Z')){
			System.out.println("Character is alphabet");
		}
		else{
			System.out.println("Character is not alphabet");
		}

	}

}

